            function ThirdPartyTracking() {

    this.getGdprConsent = function() {
        var cookies = document.cookie.split('; ').reduce(function(prev, curr) {
          var el = curr.split('=');
          prev[el[0]] = el[1];
          return prev;
        }, {});
        
        var euconsentv1 = false;
        var iterator = Object.keys(cookies);
        for (var key of iterator) {
          if (key.includes('euconsent-v2')) {
            return cookies[key];
          } else if (key.includes('euconsent')) {
            euconsentv1 = cookies[key];
          }
        } 
        return euconsentv1;
    };

    var GDPR = 0;
    var GDPR_CONSENT = '';
    if (this.getGdprConsent()){
        GDPR = 1;
        GDPR_CONSENT = this.getGdprConsent();
    }
    this.imprTags = `&quot;&quot;`;
    this.clickTags = `&quot;&quot;`;
    var tagsToParse = ['script', 'img'];

    var parseUrl = function(url) {
        var time = Date.now();
        return url.replaceAll(&quot;[timestamp]&quot;, time);
    };

    var track = function(element) {
        var tag = document.createElement(element.tagName);
        tag.height = '1';
        tag.width = '1';
        tag.style = 'border-style:none;';
        if (element.src) {
            tag.src = element.src;
        } else if (element.innerHTML) {
            tag.innerHTML = element.innerHTML;
        }
        document.body.appendChild(tag);
    };

    var decodeHtml = function(text) {
        var doc = new DOMParser().parseFromString(text, &quot;text/html&quot;);
        return doc.documentElement.textContent;
    };

    var analyseTags = function(tags) {
        if (!tags) return;

        var parsedTags = parseUrl(tags);

        var el = document.createElement('html');
        el.innerHTML = decodeHtml(parsedTags);

        tagsToParse.forEach(function(tagType) {
            var elements = el.getElementsByTagName(tagType);
            for (let element of elements) {
                track(element);
            }
        });
    };

    this.trackAction = function(action) {
        if (action == 'impression') {
            analyseTags(this.imprTags);
        } else if (action == 'click') {
            analyseTags(this.clickTags);
        }
    };
}

document.addEventListener('onOpen', function () {
    new ThirdPartyTracking().trackAction('impression');
});
document.addEventListener('onConversion', function () {
    new ThirdPartyTracking().trackAction('click');
